  private void ptb_SubtractActionPerformed(
      java.awt.event
          .ActionEvent evt) { // GEN-FIRST:event_ptb_SubtractActionPerformed
                              // TODO add your handling code here:
    String kitu = "-";
    lb_kitu.setText(kitu);
    Double a = Double.parseDouble(lb_A.getText());
    Double b = Double.parseDouble(lb_b.getText());
    Double c = Double.parseDouble(lb_c.getText());
    double result = a - b - c;
    txt_Result.setText(String.valueOf(result));

  } // GEN-LAST:event_ptb_SubtractActionPerformed

