  private void GiaiActionPerformed(
      java.awt.event.ActionEvent evt) { // GEN-FIRST:event_GiaiActionPerformed
    // TODO add your handling code here:
    Double a = Double.parseDouble(lb_A.getText());
    Double b = Double.parseDouble(lb_b.getText());
    Double c = Double.parseDouble(lb_c.getText());

    if (a == 0)
      JOptionPane.showMessageDialog(null, "ko phai pt b2, nghiem -b/c");
    else if (a > 0) {
      double delta = b * b - 4 * a * c;
      if (delta > 0) {
        double x1 = (-b + Math.sqrt(delta)) / (2 * a);
        double x2 = (-b - Math.sqrt(delta)) / (2 * a);
        value_x1.setText(String.valueOf(x1));
        value_X2.setText(String.valueOf(x2));
      } else if (delta == 0) {
        double x1 = -b / (2 * a);
        value_x1.setText(String.valueOf(x1));
        value_X2.setVisible(false);
        lb_b.setVisible(false);
      } else if (delta < 0) {
        JOptionPane.showMessageDialog(null, "PT vo nghiem");
      }
    }
  } // GEN-LAST:event_GiaiActionPerformed

